import React, { useEffect, useState } from 'react';
import axios from 'axios';

const Admin = () => {
  const [tickets, setTickets] = useState([]);
  const token = localStorage.getItem('token');

  useEffect(() => {
    axios.get('/api/admin/tickets', {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(response => {
      setTickets(response.data);
    })
    .catch(error => {
      console.error('There was an error fetching the tickets!', error);
    });
  }, [token]);

  const handleUpdate = (id) => {
    axios.put(`/api/admin/tickets/${id}`, { status: 'resolved' }, {
      headers: {
        Authorization: `Bearer ${token}`
      }
    })
    .then(response => {
      alert('Ticket updated successfully');
      setTickets(tickets.map(ticket => ticket.id === id ? response.data : ticket));
    })
    .catch(error => {
      console.error('There was an error updating the ticket!', error);
    });
  };

  return (
    <div>
      <h1>Admin Dashboard</h1>
      <ul>
        {tickets.map(ticket => (
          <li key={ticket.id}>
            {ticket.name} - {ticket.description} - {ticket.status}
            <button onClick={() => handleUpdate(ticket.id)}>Resolve</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default Admin;
